INSERT INTO `verbetes` VALUES (127,'ciclomotor','ci-clo-mo-tor ','','m.','','','veículo motorizado, com duas rodas, usado na locomoção por via, para transporte de passageiro, além do condutor. (MV) “A autorização para dirigir ciclomotores, como ‘mobiletes’, só poderá ser concedida a candidato maior de 18 anos de idade.” var. mobilete. cf. transporte','WIKIPEDIA, 2012, adaptado','',NULL,1,'','','','','','2017-12-27',NULL,8,'2016-05-25 13:59:51',5,'2018-01-04 20:13:23',1,1,NULL,NULL,NULL,NULL,NULL,NULL),(275,'reboque','re-bo-que','','m.','','','1 veículo não motorizado, com duas rodas, usado na locomoção por via, deslocado se puxado por outro, para transportar carga. (MV) “O reboque carrega até 300 kg e transportar um volume de 700 litros.” 2 tração de um veículo exercido por outro. (MV) 3 veículo para arrastar outro avariado, acidentado. (MV) “Um reboque levou o ônibus quebrado até a garagem.” 3 ação de puxar algo ou alguém. (MV) “O serviço inclui reboque do veículo, transporte do cliente decorrentes de roubo, furto ou quebra do veículo.” cf. transporte',NULL,'',NULL,1,'','','','','','2017-12-27',NULL,4,'2017-12-17 21:03:28',5,'2018-01-04 20:23:10',1,1,NULL,NULL,NULL,NULL,NULL,NULL),(276,'automóvel','au-to-mó-vel','','m.','','','veículo motorizado, movido a combustível ou à eletricidade, com quatro rodas, usado na locomoção em via, para transporte de passageiros; carro. (MV) “A média de automóveis por domicílio é de 1,5 carro.” cf. transporte',NULL,'',NULL,1,'','','','','',NULL,NULL,5,'2017-12-19 10:51:58',5,'2018-03-20 21:06:00',1,1,'','','','','',''),(278,'motoneta','mo-to-ne-ta','','f.','','','veículo motorizado, movido a combustível, com duas rodas, usado na locomoção por via, para transporte de passageiro, além do motociclista. (MV) “Fáceis de dirigir, as motonetas desagradam quem quer atingir altas velocidades.” var. lambreta, motinha, scooter, vespa. cf. transporte',NULL,NULL,NULL,1,'','','','','','2017-12-27',NULL,5,'2017-12-27 19:19:31',5,'2018-01-04 20:19:50',1,1,NULL,NULL,NULL,NULL,NULL,NULL),(279,'triciclo','tri-ci-clo','','m.','','',' 1 veículo não motorizado, movido por propulsão humana, com três rodas, usado na locomoção por via, para transporte de até três passageiros. (MV) “Visite o centro a pé, ou de triciclo.” 2 veículo motorizado, com três rodas, usado na locomoção por via, para transporte de até 2 passageiros. (MV) “Os motoqueiros usam triciclos para cortar a cidade.” 3 brinquedo que serve para transporte de criança. (MV) “As crianças levaram suas bicicletas e triciclos para transitar pelas ruas.” cf. transporte',NULL,NULL,NULL,1,'','','','','','2017-12-27',NULL,5,'2017-12-27 19:41:49',5,'2018-01-04 21:02:04',1,1,NULL,NULL,NULL,NULL,NULL,NULL),(280,'aceleração','a-ce-le-ra-ção','','f.','','','1 antecipação. (ACM) “Há possibilidade de aceleração de estudos para alunos com atraso escolar.” 2 aumento gradativo de velocidade de um veículo. “O carro da FORD venceu as seis aferições de aceleração e as oito de retomada de velocidade.” cf. transporte',NULL,NULL,NULL,1,'','','','','','2017-01-03',NULL,5,'2018-01-03 16:09:14',5,'2018-02-16 17:46:34',1,4,'','','','','',''),(281,'acelerar','a-ce-le-rar','','','','',' VTD 1 aumentar a velocidade. (ACM) “Até as 22h30, os policiais continuavam no km 34 da rodovia dos bandeirantes para tentar acelerar a velocidade dos carros.” 2 antecipar, agilizar. “O governo decidiu acelerar a distribuição dos cargos para evitar qualquer surpresa nas próximas votações”.  cf. transporte',NULL,NULL,NULL,1,'','','','','','2018-01-03',NULL,5,'2018-01-03 16:11:05',5,'2018-02-16 17:59:52',1,9,'','','','','',''),(282,'ambulância','am-bu-lân-cia','','m.','','','veículo equipado com instrumentos hospitalares para transporte e prestação de socorro a doentes e acidentados. (ACM) “Cada uma das cidades dispõe de ambulância para transportar os doentes.” ',NULL,NULL,NULL,1,'','','','','','2018-01-03',NULL,5,'2018-01-03 16:13:51',5,'2018-02-16 17:47:04',1,4,'','','','','',''),(283,'atropelamento','a-tro